var link=document.querySelector(".entry-form__ref-link"),popup=document.querySelector(".modal-content"),close=popup.querySelector(".modal-content__btn-close"),cancel=popup.querySelector(".modal-content__btn-cancel"),overlay=document.querySelector(".modal-overlay");link.addEventListener("click",function(o){o.preventDefault(),overlay.classList.add("modal-overlay-show"),popup.classList.add("modal-content-show")}),close.addEventListener("click",function(o){o.preventDefault(),overlay.classList.remove("modal-overlay-show"),popup.classList.remove("modal-content-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&popup.classList.contains("modal-content-show")&&(overlay.classList.remove("modal-overlay-show"),popup.classList.remove("modal-content-show"))}),overlay.addEventListener("click",function(o){popup.classList.contains("modal-content-show")&&(popup.classList.remove("modal-content-show"),overlay.classList.remove("modal-overlay-show"))});